    $scope.content = $rootScope.News343.content;

    $location.path("/app/News343/News343");
  };
  $scope.showButtonNext = true;
  $scope.showButtonPrev = true;
  $scope.isDisabled = false;
  $scope.next = function() {
    var len = $rootScope.News343feeds.length;
    try {

      if ($scope.index < len - 1) {
        setTimeout(function() {
          $scope.index++;
          $scope.feedDetails = $rootScope.News343feeds[$scope.index];
          $scope.showButtonPrev = true;
          $scope.$apply();
        }, 100);
      } else {
        return 0;
        if (window.device) {
          navigator.notification.alert(
            'No More News Feeds',
            null,
            '',
            'OK'
          );
        } else {
          alert('No More News Feeds')
        }
      }
    } catch (e) {
      console.log(e);
    }
    $window.scrollTo(0, 0);
  };
  $scope.prev = function() {
    try {

      if ($scope.index > 0) {
        --$scope.index;
        $scope.feedDetails = $rootScope.News343feeds[$scope.index];
        $scope.showButtonNext = true;
      } else {
        return 0;
        if (window.device) {
          navigator.notification.alert(
            'No More News Feeds',
            null,
            '',
            'OK'
          );
        } else {
          alert('No More News Feeds')
        }

      }
    } catch (e) {
      console.log(e);
    }
    $window.scrollTo(0, 0);
  };
  setTimeout(function() {
    $("#content a").on("click", function(eve) {

      var link = $(this).attr('href');
      if (link.indexOf("mailto:") > -1 || link.indexOf("tel:") > -1) {} else {
        eve.preventDefault();
		try {
      if (window.device) {
        var ref = window.open(link, "_blank", "location=no,hidden=no,enableViewportScale=yes,clearcache=yes,toolbar=yes", 'toolbarposition=top');
        ref.addEventListener('exit', function(event) {
          window.screen.orientation.lock('portrait')
        });
      }else{
        window.open($(this).attr('href'), "_blank");
      }
      } catch (e) {
        alert(e)
      }
      }
    });
  }, 500);
}

