        $.blockUI();
        var campLoc = $scope.selectedLocation;
        var campLat = campLoc.clat;
        var campLong = campLoc.clong;

        var curLat = $rootScope.mylocation.coords.latitude;
        var curLong = $rootScope.mylocation.coords.longitude;
        if ((window.device) && (window.device.platform == "iOS")) {
          //var mapLocationUrl = 'maps.apple.com/?ll=' + campLat + ',' + campLong;
          //var mapLocationUrl = "http://maps.apple.com/maps?saddr=Current+Location&daddr=" + campLat + "," + campLong + "&dirflg=d&t=m";
          var mapLocationUrl = "https://www.google.co.in/maps/dir/" + curLat + "," + curLong + "/" + campLat + "," + campLong + usertravelMode;
          $.unblockUI();
          window.open(encodeURI(mapLocationUrl), '_system', 'location=no');
        } else {

          var dirUrl = "https://www.google.co.in/maps/dir/" + curLat + "," + curLong + "/" + campLat + "," + campLong + usertravelMode;
          $.unblockUI();
          window.open(dirUrl, '_system', 'location=no');
        }
      } else {

        navigator.notification.alert("Unable to find your location. Please check your location settings");
      }

    };
  } catch (err) {
    //alert(JSON.stringify(err));
  }

}

