function pageprocessorConnect(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  $rootScope.callConnectAPI = function(url, method, data, callback) {
    try {

      if (!$rootScope.isblocking) $.blockUI();
      var apiEndPoint = $rootScope.GatewayUrl + url;
      var headers = {
        'Content-Type': 'application/json',
        'Accept': 'application/json',
        'Authorization': 'Bearer ' + $rootScope.user.accessToken,
        'X-TENANT-ID': $rootScope.user.tenant,
        'X-TENANT-DOMAIN': $rootScope.user.tenantdomain,
        'principal-user': ($rootScope.webApp) ? $rootScope.user.email : $.jStorage.get("unifyedusername")
      }


      if ($rootScope.site && $rootScope.site._id) {
        headers['site-id'] = $rootScope.site._id;
      }
      var req = {
        "method": method,
        "url": apiEndPoint,
        "headers": headers,
        data: data,
        json: true
      };


      $http(req).then(function successCallback(res) {
        if (!$rootScope.isblocking) {
          $.unblockUI();
        }

        return callback(res);
      }, function errorCallback(err) {
        console.log(JSON.stringify(err));
        if (!$rootScope.isblocking) {
          $.unblockUI();
        }
        //return callback();
        /* Commented the code below since token refersh is not working */
        if (err.status == 404) {
          return callback(null);
        } else if (err.status == 0 || err.status == 401) {
          $rootScope.refreshToken(url, method, data, callback);
          return callback();
        }
      });
    } catch (e) {
      if (!$rootScope.isblocking) {
        $.unblockUI();
      }
      console.log(e)
      return callback();
    }
  }
  $scope.showCreatePost = false;
  $scope.connectCategory = "";
  $rootScope.isblocking = false;
  let expression = /(https?:\/\/(?:www\.|(?!www))[a-zA-Z0-9][a-zA-Z0-9-]+[a-zA-Z0-9]\.[^\s]{2,}|www\.[a-zA-Z0-9][a-zA-Z0-9-]+[a-zA-Z0-9]\.[^\s]{2,}|https?:\/\/(?:www\.|(?!www))[a-zA-Z0-9]+\.[^\s]{2,}|www\.[a-zA-Z0-9]+\.[^\s]{2,})/gi;
  $scope.regex = new RegExp(expression);
  $scope.linksArray = [];
  $scope.previewData = [];
  $scope.linkPreviewsHtml = [];
  $scope.previewCards = [];
  if (window.device && !$rootScope.loggedIn) {
    navigator.notification.alert('Please login to access this applet.', null, 'Need Authentication', 'Ok');
    $location.path("/app/SignIn279/SignIn279");
    return;
  }
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $("#fixedFooterContent").html('');
  $rootScope.isblocking = false;

  $scope.setupDone = false;
  // console.log("Rootscope User:");
  // console.log($rootScope.user);

  function validateUrl(url) {
    let urlArray = url.split("//");
    if (urlArray[0] === "http:" || urlArray[0] === "https:") {
      return url;
    } else {
      return "http://" + url;
    }
  };

  $scope.setUp = function() {
    if (!$scope.setupDone) {
      $(document).on('keyup', ".addCommentInput", function(event) {
        var commentLength = $(this).val().length;
        if (commentLength == 0) {
          $(this).closest(".media-body").find(".replybtn").removeClass('themeText').addClass('replybtnText');
        } else {
          $(this).closest(".media-body").find(".replybtn").removeClass('replybtnText').addClass('themeText');
        }
      });

      $(".addCommentInput").on("keyup", function(event) {});

      if (window.device) {
        $(".hideOnApp").addClass('ng-hide');
      } else {
        $(".hideOnWeb").addClass('ng-hide');
      }


      try {

        $("#myPostImg").change(function() {
          if (this.files && this.files[0]) {
            if (this.files[0].name.split('.').pop().toLowerCase() == 'png' || this.files[0].name.split('.').pop().toLowerCase() == 'jpg' || this.files[0].name.split('.').pop().toLowerCase() == 'jpeg') {

              var FR = new FileReader();
              FR.onload = function(e) {
                //console.log(e);
                $scope.postImage = e.target.result;
                $scope.imageSource = 'camera';
                $('#postImageDiv').attr("src", $scope.postImage);
                $scope.$apply();
              };
              FR.readAsDataURL(this.files[0]);
              $("#myPostImg")[0].value = '';
            } else {
              alert("Only png, jpg file types are allowed.");
            }
          }
        });

        $("#uploadImage").on("click", function() {
          if (window.device) {
            $scope.openCamera();
          } else {
            try {
              document.getElementById("myPostImg").click();
            } catch (e) {
              console.log(e);
            }

          }
        });

        if (!window.device) {
          var observe;
          if (window.attachEvent) {
            observe = function(element, event, handler) {
              element.attachEvent('on' + event, handler);
            };
          } else {
            observe = function(element, event, handler) {
              element.addEventListener(event, handler, false);
            };
          }

          function init() {
            var text = document.getElementById('postTitle');

            function resize() {
              text.style.height = 'auto';
              text.style.height = text.height + 'px';
            }

            /* 0-timeout to get the already changed text */
            function delayedResize() {
              window.setTimeout(resize, 0);
            }

            observe(text, 'change', resize);
            observe(text, 'cut', delayedResize);
            observe(text, 'paste', delayedResize);
            observe(text, 'drop', delayedResize);
            observe(text, 'keydown', delayedResize);

            //text.focus();
            //text.select();
            resize();
          }

          init();
        }


        $scope.setupDone = true;
      } catch (E) {
        //alert(E)
      }
    }

  }



  $scope.AddTagsBtn = true;
  $scope.showAddTag = true;
  $scope.showTagsList = false;
  $scope.noOfselTags = 0;
  $scope.showUploadPhoto = false;
  $scope.image = '';
  $scope.postTitle = '';
  $scope.youtubeurl = null;
  $scope.youtubeContainer = false;
  $scope.postImage = '';
  $scope.imageSource = '';
  $scope.postTags = [];
  $scope.filterPreferance = '';
  $scope.filterPosts = [];
  $scope.emptyPost = 0;
  $scope.hideLoadMore = false;
  //$scope.enableCommentsOnConnect = false;
  // Adding rootscope since we have to use this in other pages as well
  $rootScope.enableCommentsOnConnect = false;
  $rootScope.enableLikeOnConnect = false;

  window.removeCard = (id) => {
    document.getElementById(id).remove();
  }

  $scope.generatePreviewCard = (data) => {
    let imageTemplate = "";
    let titleTemplate = "";
    let descriptionTemplate = "";
    let resultHtml = "";
    let id = "card-" + Math.random();

    let part1 = `<br><div id="${id}" class="cursorPointer list-group-item borderRadius4px clearfix marginBottom15px marginBottom10px appendUrlData" contenteditable="false" >
                             <svg onclick="removeCard('${id}')" class="filledIcon12 themeColorSvgPath appendCross" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 30 30">
                              <g data-name="Layer 2">
                                <g data-name="30-cross">
                                  <path d="M17.65,15,29.45,3.2A1.87,1.87,0,1,0,26.8.55L15,12.35,3.2.55A1.87,1.87,0,0,0,.55,3.2L12.35,15,.55,26.8A1.88,1.88,0,0,0,1.88,30a1.83,1.83,0,0,0,1.32-.55L15,17.65l11.8,11.8a1.83,1.83,0,0,0,1.32.55,1.88,1.88,0,0,0,1.33-3.2Z"></path>
                                </g>
                              </g>
                            </svg>`;
    let part2 = `<div class="col-lg-9 col-xs-8 col-sm-9 paddingLeft10">`;
    let part3 = `</div></div><br><div><br></div>`;

    if (data.image) {
      imageTemplate = `  <div class="col-lg-3 col-xs-4 col-sm-3 colPadding height100 cardImage" ng-if="">
                <img alt="Data Image" src="${data.image}">
            </div>`;
    }
    if (data.title) {
      titleTemplate = `<div class="font16 colorDark toolbar-column-active proximaSemiBoldFont " contenteditable="false"><a href="${validateUrl(data.url)}" onclick="event.stopPropagation()" target="_blank" >${data.title}</a></div>`;

    }
    if (data.description) {
      descriptionTemplate = `<div class="colorDarkGrey font12 ng-binding" contenteditable="false">${data.description}</div>`;
    }
    let urlTemplate = `<div class="colorDarkGrey font12 ng-binding" contenteditable="false">${validateUrl(data.url)} &nbsp</div>`;

    if (imageTemplate) {
      resultHtml = part1 + imageTemplate;
    } else if (!imageTemplate) {
      resultHtml = part1;
    }

    resultHtml = resultHtml + part2;

    if (titleTemplate) {
      resultHtml = resultHtml + titleTemplate;
    }
    if (descriptionTemplate) {
      resultHtml = resultHtml + descriptionTemplate;
    }
    resultHtml = resultHtml + urlTemplate;
    resultHtml = resultHtml + part3;

    div = document.getElementById('postTitle');

    div.insertAdjacentHTML('beforeend', resultHtml);


    return resultHtml;
  }

  $scope.urlsForPreview = function(prviewDataArray) {
    if (prviewDataArray.length > 0) {
      for (var i = 0; i < prviewDataArray.length; i++) {
        var dataObj = {};
        var data = prviewDataArray[i];
        if (data.title && data.description || data.image) {
          dataObj.url = data.url;
          dataObj.card = $scope.generatePreviewCard(data);
          $scope.previewCards.push(dataObj);
        } else {
          div = document.getElementById('postTitle');
          div.insertAdjacentHTML('beforeend', "<div></div>");
        }
      }
    }
  }

  $scope.fetchPreviewData = function(linksArray) {
    var data = {};
    data.links = linksArray;
    $.ajax({
      url: "/getLinkPreview",
      method: "POST",
      data: data,
      success: function(data) {
        $scope.previewData = [...data, ...$scope.previewData];
        if (data.length > 0) {
          $scope.urlsForPreview(data);
        }
      },
      error: function(e) {
        $.unblockUI();
      }
    });
  }

  $scope.onKeyUp = function(event) {
    $scope.postTitleLength = document.getElementById('postTitle').innerHTML.length;
    if ($scope.postTitleLength < 3) {
      $scope.previewData = [];
    }

    if (event.key === " " || event.key === "Enter") {
      $scope.linksArray = [];

      $scope.linksArray = document.getElementById('postTitle').textContent.match($scope.regex);
      if ($scope.linksArray != null && $scope.linksArray.length > 0) {
        if (event.key === "Enter") {
          document.execCommand('insertHTML', false, '&nbsp;');
        }
        var urls = [];
        for (var i = 0; i < $scope.linksArray.length; i++) {
          var urlToMatch = validateUrl($scope.linksArray[i]);
          if (!$scope.previewData.find(element => element.url === urlToMatch)) {
            urls.push(urlToMatch);
          }
        }
        if (urls.length > 0) {
          $scope.fetchPreviewData(urls);
        }
      }
    }
  }

  $rootScope.callConnectAPI('/unifydplatform/open/tenant/search/findOneByTenantid?tenantid=' + $rootScope.user.tenant, 'GET', '', function(res) {
    $scope.tenantInfomation = res.data;
    if ($scope.tenantInfomation.enableCommentsOnConnect) {
      $rootScope.enableCommentsOnConnect = true;
    }
    if ($scope.tenantInfomation.enableLikeOnConnect) {
      $rootScope.enableLikeOnConnect = true;
    }
  });

  var windowWidth = $(window).width();
  $(window).resize(function() {
    windowWidth = $(window).width();
  });

  $scope.userSelPreference = "Your Preferences";

  $scope.loadUserPref = function() {
    var url = '/unifyedcategory/category';

    $rootScope.callConnectAPI(url, 'GET', {}, function(res) {

      var data = res.data;

      if (data['exception']) {
        if (window.device) {
          navigator.notification.alert(
            'Unable to get preferences.', // message
            null, // callback
            '', // title
            'OK' // buttonName
          );
        } else {
          alert('Unable to get preferences.');
        }
        return;

      }
      //console.log('category ' + JSON.stringify(data));
      data['_embedded']['category'].unshift({
        "id": "all",
        "category": "All",
        "enable": true,
        "selected": false
      });

      $scope.areaOfInterest = data['_embedded']['category'];
      $rootScope.areaOfInterest = data['_embedded']['category'];

      if (!$rootScope.isUserTenantAdmin) {
        //url = '/unifyedcategory/category/search/findByWhatsupAdmins?email=' + $rootScope.user.email;
        var groups = $rootScope.user['role'].toString();
        var criteria = (groups) ? $rootScope.user.email + "," + groups : $rootScope.user.email;
        url = '/unifyedcategory/category/search/usercategories?criteria=' + criteria;
        $rootScope.callConnectAPI(url, 'GET', {}, function(res) {
          if (res.data['_embedded']['category'].length > 0) {
            $rootScope.isUserWhatsUpAdmin = true;
            var userWhatsUpAdminCategory = [];
            $scope.userWhatsUpAdminCategoryIds = [];
            for (var i = 0; i < res.data['_embedded']['category'].length; i++) {
              userWhatsUpAdminCategory.push(res.data['_embedded']['category'][i].category);
              $scope.userWhatsUpAdminCategoryIds.push(res.data['_embedded']['category'][i].id);
            }
            //console.log('userwhatsUpAdmin: ' + $scope.userWhatsUpAdminCategoryIds);
            for (var i = 0; i < $scope.areaOfInterest.length; i++) {
              $scope.areaOfInterest[i].enable = $scope.areaOfInterest[i].enable ? userWhatsUpAdminCategory.indexOf($scope.areaOfInterest[i]['category']) != -1 : false;
              $scope.areaOfInterest[i].selected = false;
            }
          }
        });
      } else {
        for (var i = 0; i < $scope.areaOfInterest.length; i++) {
          //$scope.areaOfInterest[i].enable = $scope.areaOfInterest[i].enable ? ($rootScope.user.areaOfInterest != undefined && $rootScope.user.areaOfInterest.indexOf($scope.areaOfInterest[i]['id']) != -1) : false;
          $scope.areaOfInterest[i].selected = false;
        }
      }
      //console.log($scope.areaOfInterest);
      $scope.userAOI = [];
      var url = '/unifydidentity/user/search/findOneByEmail?email=' + $rootScope.user.email;
      $rootScope.callConnectAPI(url, 'GET', {}, function(res) {
        console.log('user AOI', res.data);
        $rootScope.user.areaOfInterest = res.data.areaOfInterest;
        for (var i = 0; i < $scope.areaOfInterest.length; i++) {
          //console.log(JSON.stringify($rootScope.user.areaOfInterest) + " " + $scope.areaOfInterest[i]['id']);
          if ($rootScope.user.areaOfInterest.indexOf($scope.areaOfInterest[i]['id']) != -1) {
            $scope.userAOI.push($scope.areaOfInterest[i].category);
          }
        }
        if ($scope.userAOI.length == 0) {
          if (window.device) {
            navigator.notification.alert("Please choose your preferences.", null, ' ', 'Ok');
          } else {
            alert("Please choose your preferences.");
            $location.path("/app/Connect/ConnectPage6");
          }
        }

        $scope.loadPosts();
      });

    });
  }

  $scope.loadPosts = function(page) {
    try {
      var groups = $rootScope.user['role'].toString();
      var criteria = (groups) ? $rootScope.user.email + "," + groups : $rootScope.user.email;
      var params = "criteria=" + criteria + "&useremail=" + $rootScope.user.email + "&size=10&sort=updatedDate,DESC&page=" + page;
      if (window.device) {
        var feedUrl = "/unifydwhatsup/open/connectfeed?" + params;
      } else {
        var feedUrl = "/unifydwhatsup/open/connectfeed?platform=webapp&" + params;
      }
      $rootScope.callConnectAPI(feedUrl, 'GET', '', function(data) {
        var allcategory = data.data.allcategory;
        allcategory.unshift({
          "id": "all",
          "category": "All",
          "enable": true,
          "selected": false
        });
        $scope.areaOfInterest = allcategory;
        $rootScope.areaOfInterest = allcategory;
        if (!$rootScope.isUserTenantAdmin) {
          var myadmincategory = data.data.myadmincategory;
          if (myadmincategory.length > 0) {
            $rootScope.isUserWhatsUpAdmin = true;
            var userWhatsUpAdminCategory = [];
            $scope.userWhatsUpAdminCategoryIds = [];
            for (var i = 0; i < myadmincategory.length; i++) {
              userWhatsUpAdminCategory.push(myadmincategory[i].category);
              $scope.userWhatsUpAdminCategoryIds.push(myadmincategory[i].id);
            }
            for (var i = 0; i < $scope.areaOfInterest.length; i++) {
              $scope.areaOfInterest[i].enable = $scope.areaOfInterest[i].enable ? userWhatsUpAdminCategory.indexOf($scope.areaOfInterest[i]['category']) != -1 : false;
              $scope.areaOfInterest[i].selected = false;
            }
          }
        } else {
          for (var i = 0; i < $scope.areaOfInterest.length; i++) {
            $scope.areaOfInterest[i].selected = false;
          }
        }
        $scope.userAOI = [];
        $rootScope.user.areaOfInterest = data.data.areaOfInterest;
        for (var i = 0; i < $scope.areaOfInterest.length; i++) {
          if ($rootScope.user.areaOfInterest.indexOf($scope.areaOfInterest[i]['id']) != -1) {
            $scope.userAOI.push($scope.areaOfInterest[i].category);
          }
        }
        if ($scope.userAOI.length == 0) {
          if (window.device) {
            navigator.notification.alert("Please choose your preferences.", null, ' ', 'Ok');
          } else {
            alert("Please choose your preferences.");
            $location.path("/app/Connect/ConnectPage6");
          }
        }

        var feed = data.data.feed;
        if (feed.last) {
          $scope.hideLoadMore = false;
        } else {
          $scope.hideLoadMore = true;
        }
        for (var i = 0; i < feed.content.length; i++) {
          feed.content[i].limitComment = -3;
          feed.content[i].showViewAllComment = true;
        }
        $scope.posts = feed.content;
        $rootScope.post = feed.content;
        if ($scope.posts.length == 0) {
          $scope.emptyPost = 1;
        }
        $scope.setUp();
      });
    } catch (ex) {
      alert(ex);
    }
    setTimeout(function() {
      $(".postDesc a").on('click', function(e) {
        console.log(1)
        var url = $(this).attr('href');
        var target = $(this).attr('target');
        if (target == '_blank') {
          e.preventDefault();
          e.stopPropagation();
          window.open(url, '_blank');
        }
      });
    }, 500)
  }


  $scope.loadPosts(0);

  //$scope.loadUserPref();

  $scope.getCategory = function(arr) {
    var postCategory = [];
    for (var i = 0; i < $scope.areaOfInterest.length; i++) {
      for (var j = 0; j < arr.length; j++) {
        if ($scope.areaOfInterest[i]['id'] == arr[j]) {
          postCategory.push($scope.areaOfInterest[i]['category']);
        }
      }
    }
    if (postCategory.length == 0) {
      return "";
    } else {
      return postCategory.toString().replace(/,/g, ', ');
    }

  }

  $scope.filterPost = function(category) {
    if (category == 'all') {
      $scope.filterPreferance = ''
      $scope.userSelPreference = "Your Preferences";
    } else {
      for (var i = 0; i < $scope.areaOfInterest.length; i++) {
        //console.log(category+" "+$scope.areaOfInterest[i]['id']);
        if ($scope.areaOfInterest[i]['category'] == category) {
          $scope.filterPreferance = $scope.areaOfInterest[i]['id'].toString();
          //console.log($scope.filterPreferance);
          $scope.userSelPreference = $scope.areaOfInterest[i]['category'];
          $scope.$apply();
        }
      }
    }
  }

  $scope.likeUnlike = function(id, index) {
    /*$('#likeUnlike'+index).addClass('pulse animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function() {
     $(this).removeClass('pulse animated');
     });*/
    var postIndex;
    $scope.posts.some(function(x, i) {
      if (x.id == id) {
        postIndex = i;
        return true;
      }
    });

    /*var postIndex = $scope.posts.findIndex(function(x) {
     return x.id == id
     });*/

    $rootScope.isblocking = true;
    if ($scope.posts[postIndex].liked) {
