  $scope.loadYoutubeImage = function() {
    $scope.youtubeurl = document.getElementById('youtubeurl').value;
    if ($scope.youtubeurl == '')
      return;
    var videoId = $scope.youtube_parser($scope.youtubeurl);

    if (!videoId) {
      if (window.device) {
        navigator.notification.alert("Invalid Youtube URL", null, ' ', 'Ok');
      } else {
        //alert("Invalid youtube url.");
        $.alert({
          animation: 'none',
          title: '',
          content: '' +
            '<div tabindex="0" aria-label="Invalid Youtube URL" class="poupFocus tabFocusHighlighter">' +
            '<label>Invalid Youtube URL</label>' +
            '</div>',
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
            OK: {
              'btnClass': 'btn-primary boxradius popBtns focusTranspatent',
              'action': function() {
                //alert('cancel');
              }
            }
          }
        });
      }

      return;
    }
    $scope.youtubeClick();
    $(".poupFocus").focus();
    $scope.postImage = 'https://img.youtube.com/vi/' + videoId + '/hqdefault.jpg';
    $scope.imageSource = 'youtube';
    $scope.videoId = videoId;
    $('#postImageDiv').attr("src", $scope.postImage);
  };

  $scope.youtube_parser = function(url) {
    var regExp = /^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch\?))\??v?=?([^#\&\?]*).*/;
    var match = url.match(regExp);
    return (match && match[7].length == 11) ? match[7] : false;
  };

  $scope.youTubeImage = function(url) {
    var regExp = /^.*((youtu.be\/)|(v\/)|(\/u\/\w\/)|(embed\/)|(watch\?))\??v?=?([^#\&\?]*).*/;
    var match = url.match(regExp);
    return (match && match[7].length == 11) ? 'https://img.youtube.com/vi/' + match[7] + '/hqdefault.jpg' : false;
  }

