    if (data.description) {
      descriptionTemplate = `<div class="colorDarkGrey font12 ng-binding" contenteditable="false">${data.description}</div>`;
    }
    let urlTemplate = `<div class="colorDarkGrey font12 ng-binding" contenteditable="false">${validateUrl(data.url)} &nbsp</div>`;

    if (imageTemplate) {
      resultHtml = part1 + imageTemplate;
    } else if (!imageTemplate) {
      resultHtml = part1;
    }

    resultHtml = resultHtml + part2;

    if (titleTemplate) {
      resultHtml = resultHtml + titleTemplate;
    }
    if (descriptionTemplate) {
      resultHtml = resultHtml + descriptionTemplate;
    }
    resultHtml = resultHtml + urlTemplate;
    resultHtml = resultHtml + part3;

    div = document.getElementById('postTitle');

