  -webkit-transition: background-color 200ms ease, border 200ms ease;
  -moz-transition: background-color 200ms ease, border 200ms ease;
  transition: background-color 200ms ease, border 200ms ease;
}

.switch.active .switch-handle {
  border-color: #007aff;
  -webkit-transform: translate3d(20px, 0, 0);
  -moz-transform: translate3d(20px, 0, 0);
  transform: translate3d(20px, 0, 0);
}

.switch-transition-enabled>.switch-handle {
  -webkit-transition: -webkit-transform 200ms ease, background-color 200ms ease;
  -moz-transition: -moz-transform 200ms ease, background-color 200ms ease;
  transition: transform 200ms ease, background-color 200ms ease;
}

