function pageprocessorLookupClassesDetails(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  $scope.classforregister = $rootScope.classlookup;
  angular.forEach($scope.classforregister, function(value, key) {
    if (value[0].TIME.length > 8) {
      value[0].TIME_DISPLAY = value[0].TIME.split(" ")[1];
    } else {
      value[0].TIME_DISPLAY = "To Be Announced";
    }
    var days = value[0].TIME.indexOf(" ") != -1 ? value[0].TIME.split(" ")[0] : "";
    value[0].WEEK_DATA = [{
      "LABEL": "Mon",
      "AVAIL": days.indexOf("M") != -1,
      "DISP_CLASS": days.indexOf("M") != -1 ? "btn-success" : "btn-primary"
    }, {
      "LABEL": "Tue",
      "AVAIL": days.indexOf("T") != -1,
      "DISP_CLASS": days.indexOf("T") != -1 ? "btn-success" : "btn-primary"
    }, {
      "LABEL": "Wed",
      "AVAIL": days.indexOf("W") != -1,
      "DISP_CLASS": days.indexOf("W") != -1 ? "btn-success" : "btn-primary"
    }, {
      "LABEL": "Thu",
      "AVAIL": days.indexOf("R") != -1,
      "DISP_CLASS": days.indexOf("R") != -1 ? "btn-success" : "btn-primary"
    }, {
      "LABEL": "Fri",
      "AVAIL": days.indexOf("F") != -1,
      "DISP_CLASS": days.indexOf("F") != -1 ? "btn-success" : "btn-primary"
    }, {
      "LABEL": "Sat",
      "AVAIL": days.indexOf("S") != -1,
      "DISP_CLASS": days.indexOf("S") != -1 ? "btn-success" : "btn-primary"
    }, {
      "LABEL": "Sun",
      "AVAIL": days.indexOf("U") != -1,
      "DISP_CLASS": days.indexOf("U") != -1 ? "btn-success" : "btn-primary"
    }];
  });

  $scope.formatTime = function(fullhr) {
    var hours = fullhr.substring(0, 2);
    var minutes = fullhr.substring(2, 4);
    suffix = (parseInt(hours) >= 12) ? 'PM' : 'AM';
    hours = (parseInt(hours) > 12) ? hours - 12 : hours;
    hours = (parseInt(hours) == '00') ? 12 : hours;
    return (hours + ":" + minutes + " " + suffix);
  }

}

