    // if ($scope.creditselectedvalue == "transfer") {
    var transfer = {
      attempted: 0,
      passed: 0,
      earned: 0,
      gpa: 0,
      credit: 0
    };
    transfer.attempted = angular.isUndefined($scope.listdata.transfer_data_total[0][0].ATTEMPTED_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].ATTEMPTED_HOURS;
    transfer.passed = angular.isUndefined($scope.listdata.transfer_data_total[0][0].PASSED_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].PASSED_HOURS;
    transfer.earned = angular.isUndefined($scope.listdata.transfer_data_total[0][0].EARNED_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].EARNED_HOURS;
    transfer.gpa = angular.isUndefined($scope.listdata.transfer_data_total[0][0].GPA_HOURS) ? 0 : $scope.listdata.transfer_data_total[0][0].GPA_HOURS;
    transfer.credit = angular.isUndefined($scope.listdata.transfer_data_total[0][0].QUALITY_POINTS) ? 0 : $scope.listdata.transfer_data_total[0][0].QUALITY_POINTS;
    $scope.transfer = transfer;
    // } else if ($scope.creditselectedvalue == "institution") {
    $scope.institution = "data";
    var totalinst = {
      ah: 0,
      ph: 0,
      eh: 0,
      gpah: 0,
      qp: 0
    };
    angular.forEach($scope.listdata.institutioncredits, function(value, key) {
      this.ah += parseFloat(value.ATTEMPTED_HOURS);
      this.ph += parseFloat(value.SHRTCKG_PASS_HOURS);
      this.eh += parseFloat(value.SHRTCKG_EARN_HOURS);
      this.gpah += parseFloat(value.SHRTCKG_GPA_HOURS);
      this.qp += parseFloat(value.SHRTCKG_QUAL_PTS);
    }, totalinst);
    $scope.insttotal = totalinst;

    // } else if ($scope.creditselectedvalue == "transcripts") {
    $scope.transcripts = "data";
    angular.forEach($scope.listdata.transcripttotal, function(value, key) {
      if (value.TRANSCRIPT_TYPE == "I") {
        $scope.totalinst = value;
      } else if (value.TRANSCRIPT_TYPE == "T") {
        $scope.totaltrans = value;
      } else if (value.TRANSCRIPT_TYPE == "Z") {
        $scope.cumulative = value;
      }
    });
    // }

  }
