    if (myclass.credits) {
      if (!isNaN(myclass.credits)) {
        try {
          var myInt = parseInt(myclass.credits);
          myInt--;
          if (myInt < 0) {
            myInt = 0;
          }
          myclass.credits = myInt;
        } catch (ex) {
          myclass.credits = 0;
        }
      } else {
        myclass.credits = 0;
      }
    } else {
      myclass.credits = 0;
    }
    $scope.calculateGPA();
  }

  $scope.plusCredits = function(myclass) {
    if (myclass.credits == "0" || myclass.credits) {
      if (!isNaN(myclass.credits)) {
        try {
          var myInt = parseInt(myclass.credits);
          myInt++;

          myclass.credits = myInt;
        } catch (ex) {
          myclass.credits = 0;
        }
      } else {
        myclass.credits = 0;
      }
    } else {
      myclass.credits = 0;
    }
    $scope.calculateGPA();
  }

  $scope.changeCredit = function(myclass) {
    if (myclass.credits == "0" || myclass.credits) {
      if (!isNaN(myclass.credits)) {
        try {
          var myInt = parseInt(myclass.credits);
