      item = $scope.cumGPA;
    } else if (field == 'cumCredits') {
      item = $scope.cumCredits;
    }
    console.log("item=" + item);
    if (item == "0" || item) {
      if (!isNaN(item)) {
        try {
          var myInt = parseInt(item);
          console.log(myInt);
          if (myInt < 0) {
            console.log("resetting value back to 0");
            if (field == 'cumGPA') {
              $scope.cumGPA = 0;
            } else if (field == 'cumCredits') {
              $scope.cumCredits = 0;
            }
          }
          if (item > 100) {
