  $scope.plusCumulative = function(item) {
    var vfield = 0;
    if (item == 'cumgpa') {
      vfield = $scope.cumGPA;
    } else if (item == 'cumcredits') {
      vfield = $scope.cumCredits;
    } else {
      return;
    }

    if (vfield == "0" || vfield) {
      if (!isNaN(vfield)) {
        try {
          var myInt = parseFloat(vfield);
          myInt++;

          vfield = myInt;
        } catch (ex) {
          vfield = 0;
        }
      } else {
        vfield = 0;
      }
    } else {
      vfield = 0;
    }

    if (item == 'cumgpa') {
      $scope.cumGPA = vfield;
    } else if (item == 'cumcredits') {
      $scope.cumCredits = vfield;
    }
    $scope.calculateGPA();
  }

  $scope.minusCumulative = function(item) {
    var vfield = 0;
    if (item == 'cumgpa') {
      vfield = $scope.cumGPA;
    } else if (item == 'cumcredits') {
      vfield = $scope.cumCredits;
    } else {
      return;
    }

    if (vfield) {
      if (!isNaN(vfield)) {
        try {
          var myInt = parseFloat(vfield);
          myInt--;
          if (myInt < 0) {
            myInt = 0;
          }
          vfield = myInt;
        } catch (ex) {
          vfield = 0;
        }
      } else {
        vfield = 0;
      }
    } else {
      vfield = 0;
    }
    if (item == 'cumgpa') {
      $scope.cumGPA = vfield;
    } else if (item == 'cumcredits') {
      $scope.cumCredits = vfield;
    }
    $scope.calculateGPA();
  }

  $scope.focusstatus = "";
  $('.page-container').on("focusout", '.event-control', function() {
