  function showAlert(msg) {
    if (window.device) {
      navigator.notification.alert(msg, null, "Attendance Verification", null);
    } else {
      if ($rootScope.webapp) {
        $.alert({
          animation: 'none',
          title: 'Attendance Verification',
          content: msg,
          columnClass: 'col-lg-6 col-lg-offset-3 col-md-6 col-md-offset-3 col-sm-8 col-sm-offset-2 col-xs-10 col-xs-offset-1',
          buttons: {
