  $scope.updateAttendance = function() {
    var pidms = "";
    var att_inds = "";
    var rowcount = 0;
    angular.forEach($scope.attVerfModel.classlist, function(value, key) {
      value.ATTENDED = (value.marked) ? 'Y' : 'N';
      if (rowcount > 0) {
        pidms = pidms + ",";
        att_inds = att_inds + ",";
      }
      pidms = pidms + value.STUD_PIDM;
      att_inds = att_inds + value.ATTENDED;
      rowcount += 1;
    });
    var attDate = $scope.attVerfModel.selFormattedDate;

    var postdata = "stud_pidm=" + pidms + "&attend_ind=" + att_inds + "&attend_date=" + attDate + "&meet_id=" + $scope.attVerfModel.myclass.SURROGATE_ID + "&minutes=" + $scope.attVerfModel.myclass.MINUTES;

