  $scope.deleteConfirm = function() {
    var onConfirm = function(buttonIndex) {
      if (buttonIndex == 1) {
        $scope.deleteAttendanceData();
      }
    };
    if (window.device) {
      navigator.notification.confirm(
        'Are you sure you want to delete attendance data for this date?', // message
        onConfirm, // callback to invoke with index of button pressed
        'Delete Attendance Data', // title
        ['Yes', 'No'] // buttonLabels
      );
    } else {
      $.confirm({
        title: 'Delete Attendance Dat',
        content: 'Are you sure you want to delete attendance data for this date?',
        buttons: {
          confirm: function() {
            $scope.deleteAttendanceData();
          },
          cancel: function() {
            //
          }
        }
      });
    }

  }

