    '<a class="btn btn-default btn-top" ng-class="modflag == true ? \'active\' : \'\'" ng-click="showModules();" active-class="active"><i class="fa fa-list fa-2x"></i><br/>Modules</a>' +
    '<a class="btn btn-default btn-top" ng-class="homeflag == true ? \'active\' : \'\'" ng-click="showHome();" active-class="active"><i class="fa fa-home fa-2x"></i><br/>Home</a>' +
    '<a class="btn btn-default btn-top" ng-class="annflag == true ? \'active\' : \'\'" ng-click="showAnnouncements();"  active-class="active"><i class="fa fa-bullhorn fa-2x"></i><br/>Annoncements</a>' +
