function pageprocessorMyCourses9Page4(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  $rootScope.invokeCanvasAPI("/api/v1/courses/" + $rootScope.canvasSelCourse.id + "/modules/?include[]=items&include[]=content_details", "GET", {}, function(modules) {
    $scope.canvasSelCourcemodules = modules;
  });
  $scope.openItem = function(item) {
    if (item.type == "Page") {
      $rootScope.canvasSelModItem = item;
      $location.path("/app/MyCourses9/MyCourses9Page5");
    } else if (item.type == "File") {
      $rootScope.invokeCanvasAPI(item.url, "GET", {}, function(filedata) {
        $scope.canvasSelFileData = filedata;
        $scope.openFile(filedata);
      });
    } else if (item.type == "ExternalUrl") {
      var url = item.external_url.replace("http:", "https:");
      window.open(item.external_url, "_blank", "location=yes,hidden=no,clearcache=yes,enableViewportScale=yes");
    } else if (item.type == "ExternalTool") {
      var url = item.external_url.replace("http:", "https:");
      window.open(item.external_url, "_blank", "location=yes,hidden=no,clearcache=yes,enableViewportScale=yes");
    }
  }
  $scope.openFile = function(file) {
    window.open(file.url, "_blank", "location=yes,hidden=no,clearcache=yes,enableViewportScale=yes");
  };
}

