    var options = {
      timeout: 5000,
      frequency: 3000,
      enableHighAccuracy: true
    };
    navigator.geolocation.getCurrentPosition(function(loc) {
      var lat = loc.coords.latitude;
      var long = loc.coords.longitude;
      var data = {
        "userPhn": userphn,
        "Trackingid": trackingID,
        "User": name,
        "Lat": lat,
        "Long": long
      };
      var proxyData = data;
      $http({
        method: proxyMethod,
        url: url,
