  $rootScope.postAPI = function(endpoint, postdata, callback) {
    $.blockUI();
    var serviceurl = endpoint + "?ticket=" + $rootScope.ticket;
    var url = "";
    var proxyMethod = "POST";
    var proxyData = postdata;
    if (window.device) {
      url = serviceurl;
    } else {
      url = "/websimulator/json?url=" + serviceurl;
      proxyMethod = "POST";
      proxyData = {
        method: "POST",
        body: postdata
      }
    }
    $http({
      method: proxyMethod,
      url: url,
      data: proxyData
    }).
    success(function(data, status, headers, config) {
      $.unblockUI();
      callback(data, status, headers, config);
    }).
    error(function(data, status, headers, config) {
      $.unblockUI();
      //alert("Error in service call : " + data + " Status : " + status);
      if (status == 403) { //Ticket expired.. need to relogin
        $http.post(waurl, wadata).success(function(data, status, headers, config) {
          authSuccess(data);
          $rootScope.getAPI(endpoint, callback);
        }).error(function(data, status, headers, config) {
          authError(data);

        });
      }

    });

  };



  $rootScope.getAPI = function(endpoint, callback) {
    $.blockUI();
    var serviceurl = endpoint + "?ticket=" + $rootScope.ticket;
    var url = "";
    var proxyMethod = "GET";
    var proxyData = {};
    if (window.device) {
      url = serviceurl;
    } else {
      url = "/websimulator/json?url=" + encodeURIComponent(serviceurl);
      proxyMethod = "POST";
      proxyData = {
        method: "GET"
      }
    }
    $http({
      method: proxyMethod,
      url: url,
      data: proxyData
    }).
    success(function(data, status, headers, config) {
      $.unblockUI();
      callback(data, status, headers, config);
    }).
    error(function(data, status, headers, config) {
      $.unblockUI();
      //alert("Error in service call : " + data + " Status : " + status);
      if (status == 403) { //Ticket expired.. need to relogin
        $http.post(waurl, wadata).success(function(data, status, headers, config) {
          authSuccess(data);
          $rootScope.getAPI(endpoint, callback);
        }).error(function(data, status, headers, config) {
          authError(data);
        });
      }
    });
  };

  /*get and post API for Webadvisor - end */

}

function pageprocessorAppLauncher(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);

  if ($rootScope.loggedIn) {
    $scope.userRoles = $rootScope.user.role.map(function(r) {
      return r.toLowerCase();
    });
  } else {
    $scope.userRoles = [];
  }


  $scope.links = pagedef.appsList;

  $scope.checkUserHasAccess = function(role) {
    role = role.toLowerCase().split(',');
    var found = false;
    $scope.userRoles.forEach(function(val) {
      if (role.indexOf(val) != -1) {
        found = true;
      }
    });
    return found;
  }

  $scope.launchApp = function(app) {
    var appInfo = app;
    if (window.device.platform == "iOS") {
      var iosUri = appInfo.iOSUrlSchema;
      window.plugins.launcher.canLaunch({
        uri: iosUri
      }, function() {
        window.plugins.launcher.launch({
          uri: iosUri
        });
      }, function() {
        $scope.loadurl(appInfo.iOSUrl);
      });
    } else if (window.device.platform == "Android") {
      var androidpackage = appInfo.androidPackage
      window.plugins.launcher.canLaunch({
        packageName: androidpackage
      }, function() {
        window.plugins.launcher.launch({
          packageName: androidpackage
        });
      }, function() {
        $scope.loadurl(appInfo.androidUrl);
      });
    } else {
      apprise("This feature is not available on Emulator", {
        "verify": false,
        "textYes": "Ok"
      }, function(r) {});
    }
  }


  $scope.loadurl = function(url) {
    if (window.device) {
      $.blockUI();
    }
    var iabRef = window.open(url, device.platform == "Android" ? "_system" : "_blank", "location=no,hidden=yes");
    var hideBlockUi = function() {
      iabRef.show();
      $.unblockUI();
    };
    var iabClose = function(data) {
      //window.location.href = "index.html#home";
      $.unblockUI();
    };
    iabRef.addEventListener("exit", iabClose);
    var loadStop = function(data) {
      hideBlockUi();
    };
    iabRef.addEventListener("loadstop", loadStop);
  }
}

function pageprocessorRCCCSignIn(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {

  var data1 = $compile($(pagedef.datatemplate))($scope);
  $("#appContent").append(data1);
  try {
    $rootScope.back = function() {
      if ($.jStorage.get('tenantInfo').guestApp) {
        $location.path("/app/Menu82/Menu82");
      } else {
        // this should be rechecked if menu page should be displayed when guestapps are disabled
        $location.path("/app/Menu82/Menu82");
      }
    }


    $scope.termsServices = pagedef.termServices;
    $scope.policy = pagedef.policy;
    $scope.forgotPass = pagedef.forgotPass;


    $scope.webAdvisorLogin = function() {
      $.blockUI();
      var waUname = $scope.wausername;
      var waPassword = $scope.wapassword;

      var url = "https://kryptosmw.kryptosmobile.com/webadapter2/services/authenticate/rcccprod/WebAdvisor";
      var data = {};
      waUname = waUname.replace("@roanokechowan.edu", "");
      if (window.device) {
        data = "username=" + waUname + "&password=" + waPassword;
      } else {
        url = "/websimulator/json?url=" + url;
        data = {
          method: "POST",
          body: "username=" + waUname + "&password=" + waPassword
        };


      }


      /*$.blockUI({
        message: '<div id="floatingBarsG"><div class="blockG" id="rotateG_01"></div><div class="blockG" id="rotateG_02"></div><div class="blockG" id="rotateG_03"></div><div class="blockG" id="rotateG_04"></div><div class="blockG" id="rotateG_05"></div><div class="blockG" id="rotateG_06"></div><div class="blockG" id="rotateG_07"></div><div class="blockG" id="rotateG_08"></div></div><div><h3 style="color:#fff;">Authenticating</h3></div>'
      });*/

      $http.post(url, data).success(function(data, status, headers, config) {

        $rootScope.ticket = data.ticket;
        if ($rootScope.ticket) {
          $.jStorage.set('WAUsername', $scope.wausername);
          $.jStorage.set('WAPassword', $scope.wapassword)
          $rootScope.isLoggedInToWA = true;
          setTimeout(function() {
            $.unblockUI();
            window.location.href = "index.html#/app/Banner10/Banner10";
            //$location.path("/app/Banner10/Banner10");
          }, 1000);
        } else {
           if (window.device) {
             $.unblockUI();
          navigator.notification.alert(
            'Please check your username/password', // message
            null, // callback
            'Login failed', // title
            'OK' // buttonName
          );
        } else {
          alert('Please check your username/password')
        }
        }



      }).error(function(data, status, headers, config) {
        $.unblockUI();
        if (errorcb) {
          errorcb(data);
        }
      });
    }



    $scope.openForgotPassword = function() {
      $location.path("/app/ForgotPassword2/ForgotPassword2");
    };

    $scope.waLogin = function() {

      if ($scope.wausername == '') {

        if (window.device) {
          navigator.notification.alert(
            'Missing username', // message
            null, // callback
            'Error', // title
            'OK' // buttonName
          );
        } else {
          alert('Missing username')
        }

        return;
      }
      if ($scope.wapassword == '') {

        if (window.device) {
          navigator.notification.alert(
            'Missing password', // message
            null, // callback
            'Error', // title
            'OK' // buttonName
          );
        } else {
          alert('Missing password')
        }

        return;
      }


      $scope.webAdvisorLogin();


    };


    $scope.openUrl = function(url) {
      window.open(url, '_blank');
    }

    $scope.$on("$destroy", function() {
      $(".backButtonArrow").show();
    });

    $scope.waopenForgotPass = function() {
      if ($scope.forgotPass) {
        window.open($scope.forgotPass, '_blank');
      } else {
        window.open('https://myrccc.roanokechowan.edu/myRCCC/', '_blank');
      }
    }

  } catch (e) {
    alert(e)
  }
}

