      $scope.hasmore = true;
    } else {
      $scope.hasmore = false;
    }
    $scope.nextpage = parseInt(split[1]) + 1;
  }
  $scope.checkmore();
  $scope.nextPage = function() {
    $.blockUI();
    var url = $rootScope.middlewareServerUrl + "/colleaguecssearchpage";
    var params = $rootScope.searcparams + "&pagenum=" + $scope.nextpage;
    $rootScope.postAPI(url, params, function(data) {
