function pageprocessorSignIn279(pagedef, $scope, $routeParams, $compile, $http, $rootScope, $sce, $window, $location) {
    try {
        $rootScope.back = function() {
            if ($.jStorage.get('tenantInfo').guestApp) {
                $location.path("/app/Menu82/Menu82");
            } else {
                // this should be rechecked if menu page should be displayed when guestapps are disabled
                $location.path("/app/Menu82/Menu82");
            }
        }

        //if (!$.jStorage.get('tenantInfo').guestApp) {
        //  $(".backButtonArrow").hide();
        //}

        $scope.termsServices = pagedef.termServices;
        $scope.policy = pagedef.policy;
        $scope.forgotPass = pagedef.forgotPass;

        $scope.username = '';
        $scope.password = '';
        var authSuccess = function(data) {
            if (!data.access_token) {
                $.unblockUI();
                if (window.device) {
                    navigator.notification.alert(
                        'Unable to login, please try again', // message
                        null, // callback
                        'Error', // title
                        'OK' // buttonName
                    );
                } else {
                    alert('Unable to login, please try again')
                }
                return;
            }


            if (data.status && data.status == 500) {
                $.unblockUI();
                if (window.device) {
                    navigator.notification.alert(
                        'Invalid credentials', // message
                        null, // callback
                        'Error', // title
                        'OK' // buttonName
                    );
                } else {
                    alert('Invalid credentials')
                }
                return;
            }

            $.jStorage.set("unifyedusername", $scope.username);
            $.jStorage.set("unifyedpassword", $scope.password);

            $rootScope.user['accessToken'] = data.access_token;
            $rootScope.user['refreshToken'] = data.refresh_token;
            $rootScope.user['providerData'] = data.access_token;
            $rootScope.erpticket = {};
            $rootScope.erpticket[$.jStorage.get('tenantInfo').erp.name] = data.access_token;
            $rootScope.loggedIn = true;
            if ($.jStorage.get("homeScreen") == null || $.jStorage.get("homeScreen") == undefined) {
                $.jStorage.set("homeScreen", "whatsup");
                $rootScope.homeScreen = "whatsup";
            } else {
                $rootScope.homeScreen = $.jStorage.get("homeScreen");
            }

            // Draw dock icons
            angular.forEach($rootScope.appgroups, function(value, key) {
                angular.forEach(value.apps, function(val1, key1) {
                    if (val1.displayname == "WhatsUp") {
                        val1.showInDock = ($rootScope.homeScreen == "whatsup") ? true : false;
                    } else if (val1.displayname == "QuickLaunch") {
                        val1.showInDock = val1.showInDock = ($rootScope.homeScreen == "ql") ? true : false;
                    }
                });
            });
            // Redraw the dock icons
            var data2 = '<div ng-repeat="ag in appgroups">' +
                '<div ng-repeat="app in ag.apps" style="float:left;width:20%;text-align:center;" ng-show="app.showInDock" class="div{{app.displayname}}">' +
                '<a ng-if="app.displayname != \'Notifications\'" aria-label="{{app.displayname}}" href="#/app/{{app.name}}/{{app.name}}" class="divA{{app.displayname}}" style="width: 100%;display: block;">' +
                '<img style="width:20px;" src="{{app.logo}}" alt="">' +
                '</a></div>' +
                '<div class="clearfix"></div>'
            '</div>';
            //var htmlView = $compile($(data2))($scope);

            //$("#footerContentContainer").html(htmlView);

            var options = {
                method: 'GET',
                url: $rootScope.user.oauthUserInfoUrl,
                qs: {
                    schema: 'openid'
                },
                headers: {
                    Authorization: 'Bearer ' + $rootScope.user.accessToken
                }
            };

            $rootScope.callOpenAPI(options, function(err, userInfo) {

                if (err) {
                    $.unblockUI();
                    if (window.device) {
                        navigator.notification.alert(
                            'Unable to login, please try again', // message
                            null, // callback
                            'Error', // title
                            'OK' // buttonName
                        );
                    } else {
                        alert('Unable to login, please try again')
                    }
                    return;
                }


                var subVal = userInfo.sub;
                $rootScope.username = "";
                if (subVal) {
                    var userSplit = subVal.split('/');
                    $rootScope.username = (userSplit.length == 1) ? userSplit[0].split('@')[0] : userSplit[1].split('@')[0];
                    console.log("user sub= " + $rootScope.username);
                }

                $rootScope.password = $scope.password;

                $.jStorage.set("userSub", userInfo.sub);

                $rootScope.user['email'] = userInfo.email;
                $rootScope.user['firstName'] = userInfo.first_name || userInfo.given_name;
                $rootScope.user['lastName'] = userInfo.last_name || userInfo.family_name;
                try {
                    // alert(userInfo.groups)
                    if (typeof userInfo.groups == "string") {
                        $rootScope.user.role = userInfo.groups.match(/(?:CN=)(\w+)-*[^,]*(\s)*(\w+)/g).toString().replace(/CN=/g, "").split(",");
                    } else if (typeof userInfo.groups == "object") {
                        $rootScope.user.role = []
                        $.each(userInfo.groups, function(key, value) {

                            $rootScope.user.role.concat(value.match(/(?:CN=)(\w+)-*[^,]*(\s)*(\w+)/g).toString().replace(/CN=/g, "").split(","))
                        });
                    } else {
                        $rootScope.user.role = []
                        console.log("Error: The user has no roles !");
                    }

                } catch (ex) {
                    $rootScope.user.role = [];
                    console.log("Error while fetching roles from QL groups:");
                    console.log(ex);
                }

                $rootScope.isUserTenantAdmin = $rootScope.user.admins.indexOf($rootScope.user['email']) !== -1;

